import { css, customElement, html, LitElement, property } from 'lit-element';

export enum Themes {
    default = 1
}

@customElement("ukjp-terminal")
export class Termina extends LitElement {
    @property({type: Number}) theme : number = Themes.default; 
    static get styles() {
        return css`
       @keyframes blink {
    0% {
    }
    51% {
        opacity: 0%;
        transform: rotateY(180deg);
    }
    100% {
    }
}
#terminal {
    width: 100vw;
    height: 40vh;
    padding-top: 10px;
    background: #232323;
    line-height: 12px;
    color: #eecc22;;
    font-family : "SourceCode", monospace;
}
#terminal b {
        color: #FF2222;
    }
#terminal u {
        color: #22FF22;
    }
#terminal i {
        color: #2222FF;
    }
    span {
        display: inline;
    }
    span::before {
            content : "λ "
        }
    span .input {
            display: inline;
        }
    span .cursor {
            display: inline-block;
            border: 2px solid white;
            width: 6px;
            height: 12px;
            animation-name: blink;
            animation-duration: 2s;
            animation-iteration-count: infinite;
            animation-fill-mode: both;
            animation-direction: normal;
            animation-timing-function: ease-in-out;
            animation-play-state: running;
        } 
        `
    }
    render() {
        let useTheme : string = "default";
        switch(this.theme) {
            case Themes.default:
                useTheme = "default";
                break;
        }
        return html`
        <section id="term" class="${useTheme}">
            <div id="terminal">
                <div id="history"></div>
                <span>
                    <div class="input"></div>
                    <div class="cursor"></div>
                </span>
            </div>
        </section>
        `;
    }
}
